#include<stdio.h>
#include<math.h>

int main(){

    // points are (x1,y1),(x2,y2),(x3,y3)
    double x1, x2, x3;
    double y1, y2, y3;
    scanf("%lf%lf%lf%lf%lf%lf",&x1,&y1,&x2,&y2,&x3,&y3);

    double m1, m2;
    m1 =(y2-y1)/(x2-x1);
    m2 =(y3-y2)/(x3-x2);

    //condition of collinearity; if the three points lie on a straight line

    if(m1==m2){
        printf("Triangle coud not be formed; the points are collinear\n\n\n");
        return 0;
    }

    double a, b, c;
    //length of the side opposite to pint (x1,y1) is a
    //length of the side opposite to pint (x2,y2) is b
    //length of the side opposite to pint (x3,y3) is c

    a=sqrt( (x3-x2)*(x3-x2)+(y3-y2)*(y3-y2));
    b=sqrt( (x1-x3)*(x1-x3)+(y1-y3)*(y1-y3));
    c=sqrt( (x2-x1)*(x2-x1)+(y2-y1)*(y2-y1));


    double A, B, C;  // angle of the triangle are A, B, c
    A =acos((b*b+c*c-a*a)/(2*b*c));
    B =acos((a*a+c*c-b*b)/(2*a*c));
    C =acos((a*a+b*b-c*c)/(2*a*b));

    // Angles A, B, and C calculated are in radians
    // 1 radian =57.295779 degrees

    const double degree= 57.295779;

    printf("%lf degrees\n",A*degree);
    printf("%lf degrees\n",B*degree);
    printf("%lf degrees\n\n\n",C*degree);

    return 0;

}

