#include <stdio.h>
#include <math.h>

int main(){
    // Task 1
    float x1,x2,y1,y2;
    printf("Write x1: ");
    scanf("%f",&x1);
    printf("Write y1: ");
    scanf("%f",&y1);
    printf("Write x2: ");
    scanf("%f",&x2);
    printf("Write y2: ");
    scanf("%f",&y2);

    //Task 2
    float a=1.0; // 'a' is assigned a value which is used to find b and c
    float slope=(y2-y1)/(x2-x1);
    float b=-(a/slope);
    float c=-(a*x1+b*y1);
    printf("Equation of the line joining the two points is %fX+(%fY)+(%f)=0\n",a,b,c);

    //Task 3
    float distance=fabs(c/(sqrt(a*a+b*b))); // Distance is always positive so applied fabs()
    printf("The distance of the line from the origin is %f\n",distance);

    //Task 4
    float mirror_x_0=-(2*a*c)/(a*a+b*b);
    float mirror_y_0=-(2*b*c)/(a*a+b*b);
    printf("Mirror image of the origin with respect to the line is (%f,%f)\n",mirror_x_0,mirror_y_0);

    //Task 5
    float x3,y3;
    printf("Write x3: ");
    scanf("%f",&x3);
    printf("Write y3: ");
    scanf("%f",&y3);
    float result=fabs((a*x3+b*y3+c)); // Applied fabs() keeping in mind 0.0 and -0.0
    if(result==0.0f){
        printf("The point (%f,%f) lies on the straight line\n",x3,y3);
    }
    else{
        printf("The point (%f,%f) does not lie on the straight line\n",x3,y3);
    }

    //Task 6
    float mirror_x_x3=x3-((2*a*result)/(a*a+b*b));
    float mirror_y_y3=y3-((2*b*result)/(a*a+b*b));
    printf("Mirror image of (%f,%f) with respect to the line is (%f,%f)",x3,y3,mirror_x_x3,mirror_y_y3);

    return 0;
}

#include <stdio.h>
#include <math.h>

int main(){
    float x,SUM=0.0,PRODUCT=1.0;
    int n;
    printf("Enter x (floating point number) : ");
    scanf("%f",&x); // x is taken as input from user
    printf("Enter n (integer) : ");
    scanf("%d",&n); // n is taken as input from user
    if(n>=0 && n<=10){
        SUM=(x*(n+1))+((n*(n+1))/2); // (a) Calculating sum as per the given series using some math formulas
    }
    /* (b) Calculating product as per the given series */
    /* Control enters in switch case as per the value of n
       and starts to calculate the series backwards from that value of n towards the first term
    */
    switch(n){
        case 10:
            PRODUCT=PRODUCT*(x+10);
        case 9:
            PRODUCT=PRODUCT*(x+9);
        case 8:
            PRODUCT=PRODUCT*(x+8);
        case 7:
            PRODUCT=PRODUCT*(x+7);
        case 6:
            PRODUCT=PRODUCT*(x+6);
        case 5:
            PRODUCT=PRODUCT*(x+5);
        case 4:
            PRODUCT=PRODUCT*(x+4);
        case 3:
            PRODUCT=PRODUCT*(x+3);
        case 2:
            PRODUCT=PRODUCT*(x+2);
        case 1:
            PRODUCT=PRODUCT*(x+1);
        case 0:
            PRODUCT=PRODUCT*x;
            break;
        default:
            printf("The value of n should be an integer from 0 to 10 both inclusive!\n");
    }
    float y;
    char operator,holder;
    float result=0;
    printf("Enter y (positive floating point number) : ");
    scanf("%f",&y); // (c) y is taken as input from user

    /* holder is used to hold the null terminator character
       after input of y which otherwise gets stored in operator variable.
       apart from this, holder has no use as such in this program
    */
    scanf("%c",&holder);

    /* Start of part (d) */
    printf("Type + for addition.\n");
    printf("Type - for subtraction.\n");
    printf("Type * for multiplication.\n");
    printf("Type / for division.\n");
    printf("Type percentage sign for remainder.\n");
    printf("Enter an operator: ");
    scanf("%c",&operator); // operator to be used in calculator is taken as input from user
    switch(operator){
        case '+':
            result=x+y; // addition
            break;
        case '-':
            result=x-y; // subtraction
            break;
        case '*':
            result=x*y; // multiplication
            break;
        case '/':
            result=(int)(x/y); // division
            break;
        case '%':
            result=fmod(x,y); //remainder
            break;
        default:
            printf("Sorry you have entered an invalid operator!\n");
    }
    /* Entering part (e) */
    printf("The value of x is: %f\n",x);
    printf("The sum is: %f\n",SUM);
    printf("The product is: %f\n",PRODUCT);
    printf("The floating point number obtained from the calculator is: %f\n",result);

    return 0;
}

